A cautious mean

my_mean &lt;- function(x, na.rm = TRUE) {
  if (na.rm) {
    if (any(is.na(x))) {
      print("Watch out, you are removing NAs")
    } else {
      print("We didn't remove any NAs")
    }
    x &lt;- x[!is.na(x)]
  } 

  average &lt;- sum(x) / length(x)
  return(average)
}

